func search(nums []int, target int) int {
	// write code here
	if len(nums) == 0 {
		return -1
	}

	l := 0
	r := len(nums) - 1
	for l <= r {
		mid := (l + r) >> 1
		if nums[mid] == target {
			//处理重复
			for mid != 0 && nums[mid-1] == target {
				mid--
			}
			return mid
		} else if nums[mid] > target {
			r = mid - 1
		} else {
			l = mid + 1
		}
	}

	return -1
}

